import unittestimport jsonimport loggingimport sysimport inspectimport osfrom ddt import ddt, datafrom customassertions import CustomAssertionsfrom mockFields import field_mapfrom pprint import pprintcmd_folder = os.path.abspath(os.path.join(os.path.split(inspect.getfile(    inspect.currentframe()))[0], ".."))if cmd_folder not in sys.path:    sys.path.insert(0, cmd_folder)from rejira.lib.issue import Issuefrom example import configdef str_to_bool(s):    if s == 'True':        return True    elif s == 'False':        return False    else:        raise ValueError@ddtclass ReJIRAUnitTest(unittest.TestCase, CustomAssertions):    def setUp(self):        self.config = self.setup_config()        self.logging = self.setup_logging()    def tearDown(self):        del self.config        del self.logging    @staticmethod    def get_req(file_name):        with open(file_name, 'r') as file:            req = file.read()            file.close()        return json.loads(req)    def create_issue(self):        return Issue(self.config, self.logging)    @staticmethod    def setup_config():        config.cache_host = os.getenv('REJIRA_CACHE_HOST', 'localhost')        config.cache_port = os.getenv('REJIRA_CACHE_PORT', 6379)        config.cache_db = os.getenv('REJIRA_CACHE_DB', 0)        config.cache_on = str_to_bool(os.getenv('REJIRA_CACHE_ON', True))        config.cache_expire = os.getenv('REJIRA_CACHE_EXPIRE', 3600)        config.jira_user = os.getenv('REJIRA_JIRA_USER', '')        config.jira_pass = os.getenv('REJIRA_JIRA_PASS', '')        config.jira_options["server"] = os.getenv('REJIRA_JIRA_SERVER', '')        config.logging_level = os.getenv('REJIRA_LOGGING_LEVEL', 'ERROR')        config.logging_file = os.getenv('REJIRA_LOGGING_FILE', '')        return config    def setup_logging(self):        logging.basicConfig(level=self.config.logging_level)        logger = logging.getLogger(__name__)        if self.config.logging_file:            handler = logging.FileHandler(self.config.logging_file)            handler.setLevel(logging.INFO)            formatter = logging.Formatter('%(asctime)s - %(name)s - %(levelname)s - %(message)s', "%Y-%m-%d %H:%M:%S")            handler.setFormatter(formatter)            logger.addHandler(handler)        return logger    def test_issue_close(self):        issue = self.create_issue()        issue.close()        self.assertAttrNotExists(issue, "config")        self.assertAttrNotExists(issue, "logger")    @data('mock-req-1.txt', 'mock-req-2.txt', 'mock-req-3.txt')    def test_issue_handle_sprint(self, file_name):        req = self.get_req(file_name)        issue = self.create_issue()        sprint_obj = issue.handle_sprint(req["fields"])        if file_name != 'mock-req-2.txt':            self.assertAttrExists(sprint_obj, "name")            self.assertAttrExists(sprint_obj, "state")            self.assertAttrExists(sprint_obj, "completeDate")            self.assertEqual(sprint_obj.name, 'OM Sprint 1')            self.assertEqual(sprint_obj.state, 'ACTIVE')            self.assertEqual(sprint_obj.completeDate, '<null>')        else:            self.assertIsNone(sprint_obj)    @data('mock-req-1.txt')    def test_issue_handle_simple_list(self, file_name):        req = self.get_req(file_name)        issue = self.create_issue()        ret_list = issue.handle_list(req["fields"]["components"], {'fields': {'name': None}})        self.assertIsList(ret_list)        self.assertIsInList('comp1', ret_list)    @data('mock-req-1.txt')    def test_issue_handle_complex_list(self, file_name):        req = self.get_req(file_name)        issue = self.create_issue()        ret_list = issue.handle_list(req["fields"]["components"], {'fields': {'name': None, 'id': None}})        self.assertIsList(ret_list)        self.assertIsObject(ret_list[0])        self.assertAttrExists(ret_list[0], 'name')        self.assertEqual(ret_list[0].name, 'comp1')    @data('mock-req-1.txt')    def test_issue_handle_dict(self, file_name):        req = self.get_req(file_name)        issue = self.create_issue()        obj = issue.handle_dict(req["fields"]["creator"])        self.assertAttrExists(obj, "key")        self.assertAttrExists(obj, "name")        self.assertAttrExists(obj, "displayName")        self.assertEqual(obj.name, 'admin')        self.assertEqual(obj.displayName, 'Ryan Meinzer')    @data('mock-req-1.txt')    def test_issue_handle_custom(self, file_name):        req = self.get_req(file_name)        issue = self.create_issue()        obj = issue.handle_custom(req, field_map["custom"])        self.assertEqual(obj.epic, 'OM-2')        self.assertIsNone(obj.customfield_10024)        self.assertEqual(obj.customfield_10026, 'some string')        self.assertIsObject(obj.customfield_10027[0])        self.assertIsList(obj.customfield_10027)        self.assertEqual(obj.customfield_10027[0].name, 'admin')        self.assertIsNone(obj.checkbox)    @data('mock-req-1.txt')    def test_issue_handle_dates(self, file_name):        req = self.get_req(file_name)        issue = self.create_issue()        obj = issue.handle_dates(req, field_map["dates"])        self.assertIsObject(obj)        self.assertAttrExists(obj, "created")        self.assertAttrExists(obj, "due")        self.assertIsNone(obj.due)        self.assertEqual(obj.created.year, 2017)    @data('mock-req-1.txt')    def test_issue_handle_comments(self, file_name):        req = self.get_req(file_name)        issue = self.create_issue()        comments = issue.handle_comments(req, field_map["comments"])        self.assertIsList(comments)        self.assertIsObject(comments[0])        self.assertEqual(len(comments), 2)        self.assertEqual(comments[0].body, 'Comment 1')        self.assertAttrNotExists(comments[0], 'updated')        self.assertIsObject(comments[0].author)        self.assertEqual(comments[0].author.displayName, 'Ryan Meinzer')    @data('mock-req-1.txt', 'mock-req-2.txt', 'mock-req-3.txt')    def test_issue_create_object(self, file_name):        req = self.get_req(file_name)        issue = self.create_issue()        issue.create_object(req, field_map)        self.assertAttrExists(issue, 'key')if __name__ == '__main__':    unittest.main()