import unittestimport jsonimport loggingimport sysimport inspectimport osfrom customassertions import CustomAssertionsfrom mockFields import field_mapcmd_folder = os.path.abspath(os.path.join(os.path.split(inspect.getfile(    inspect.currentframe()))[0], ".."))if cmd_folder not in sys.path:    sys.path.insert(0, cmd_folder)from rejira.lib.issue import Issuefrom example import config# test issue.close() firstclass ReJIRATest(unittest.TestCase, CustomAssertions):    def setUp(self):        with open('tests/mock-req-1.txt', 'r') as file:            data = file.read()            file.close()        req = json.loads(data)        self.issue = Issue(config, logging)        self.issue.create_object(req, field_map)        #pprint(vars(self.issue.sprint))    def test_issue_close(self):        self.assertAttrNotExists(self.issue, "config")        self.assertAttrNotExists(self.issue, "logger")    def test_handle_sprint(self):        self.assertAttrExists(self.issue, "sprint")        self.assertEqual(self.issue.sprint.name, 'OM Sprint 1')        self.assertEqual(self.issue.sprint.state, 'ACTIVE')        self.assertEqual(self.issue.sprint.completeDate, '<null>')if __name__ == '__main__':    unittest.main()