import unittestimport jsonimport loggingimport sysimport inspectimport osfrom customassertions import CustomAssertionsfrom mockFields import field_mapcmd_folder = os.path.abspath(os.path.join(os.path.split(inspect.getfile(    inspect.currentframe()))[0], ".."))if cmd_folder not in sys.path:    sys.path.insert(0, cmd_folder)from rejira.lib.issue import Issuefrom example import configclass ReJIRATest(unittest.TestCase, CustomAssertions):    def setUp(self):        self.config = self.setup_config()        self.logging = self.setup_logging()        with open('mock-req-1.txt', 'r') as file:            data = file.read()            file.close()        self.req = json.loads(data)    def create_issue(self):        return Issue(self.config, self.logging)    @staticmethod    def setup_config():        config.cache_host = os.getenv('REJIRA_CACHE_HOST', 'localhost')        config.cache_port = os.getenv('REJIRA_CACHE_PORT', 6379)        config.cache_db = os.getenv('REJIRA_CACHE_DB', 0)        config.cache_on = os.getenv('REJIRA_CACHE_ON', True)        config.cache_expire = os.getenv('REJIRA_CACHE_EXPIRE', 3600)        config.jira_user = os.getenv('REJIRA_JIRA_USER', '')        config.jira_pass = os.getenv('REJIRA_JIRA_PASS', '')        config.jira_options["server"] = os.getenv('REJIRA_JIRA_SERVER', '')        config.logging_level = os.getenv('REJIRA_LOGGING_LEVEL', 'ERROR')        config.logging_file = os.getenv('REJIRA_LOGGING_FILE', '')        return config    def setup_logging(self):        logging.basicConfig(level=self.config.logging_level)        logger = logging.getLogger(__name__)        if self.config.logging_file:            handler = logging.FileHandler(self.config.logging_file)            handler.setLevel(logging.INFO)            formatter = logging.Formatter('%(asctime)s - %(name)s - %(levelname)s - %(message)s', "%Y-%m-%d %H:%M:%S")            handler.setFormatter(formatter)            logger.addHandler(handler)        return logger    def test_issue_close(self):        issue = self.create_issue()        issue.create_object(self.req, field_map)        self.assertAttrNotExists(issue, "config")        self.assertAttrNotExists(issue, "logger")    def test_handle_sprint(self):        issue = self.create_issue()        sprint_obj = issue.handle_sprint(self.req["fields"])        self.assertAttrExists(sprint_obj, "name")        self.assertAttrExists(sprint_obj, "state")        self.assertAttrExists(sprint_obj, "completeDate")        self.assertEqual(sprint_obj.name, 'OM Sprint 1')        self.assertEqual(sprint_obj.state, 'ACTIVE')        self.assertEqual(sprint_obj.completeDate, '<null>')    def test_smoke_sprint(self, ):        issue = self.create_issue()        issue.create_object(self.req, field_map)        self.assertAttrExists(issue, "sprint")        self.assertEqual(issue.sprint.name, 'OM Sprint 1')        self.assertEqual(issue.sprint.state, 'ACTIVE')        self.assertEqual(issue.sprint.completeDate, '<null>')if __name__ == '__main__':    unittest.main()